/*
*  Name_file :HomeStudent.js
*  Description: Pagina home del estudiante,contiene la vista del estudiante
*    
*/
import React, { Component } from 'react';
import Cookies from 'universal-cookie';
import axios from 'axios';

const baseUrl = "http://localhost:3001/links/api/getGroupStudent";
const cookies = new Cookies();

class HomeStudent extends Component 
{

    state = {
        data: [],

    }

    /*Se hacen peticiones al servidor para que me devuelva la tabla grupoestudiante*/
    peticionGet = () => 
    {
        axios.get(baseUrl, { params: { idEstudiante: cookies.get('id') } }).then(response => {
            console.log(response.data);//muestra consola navegador
            this.setState({ data: response.data });
        }).catch(error => {
            console.log(error.message);
        })
    }

    /*Elimina los datos de sesion almacenada por las cookies*/
    cerrarSesion = () => 
    {
        cookies.remove('id', { path: "/" });
        cookies.remove('correo', { path: "/" });
        cookies.remove('nombre', { path: "/" });
        cookies.remove('apellidos', { path: "/" });
        cookies.remove('foto', { path: "/" });
        cookies.remove('activo', { path: "/" });
        cookies.remove('rol', { path: "/" });
        window.location.href = './';
    }

    /*Si vuelvo a la pagina de login, comprueba si el usuario ya inicio sesion anteriomente
    si es el caso lo redirige a la home segun su rol*/
    componentDidMount() 
    {
        this.peticionGet();
        if (!cookies.get('correo'))
        {
            window.location.href = "./";
        }
    }

    /*Dibuja la pagina */
    render() 
    {
        console.log('id: ' + cookies.get('id')); //mensaje por consola navegador
        console.log('correo: ' + cookies.get('correo'));
        console.log('nombre: ' + cookies.get('nombre'));
        console.log('apellidos: ' + cookies.get('apellidos'));
        console.log('foto: ' + cookies.get('foto'));
        console.log('activo: ' + cookies.get('activo'));
        console.log('rol: ' + cookies.get('rol'));
        return (
            <>
                <h2> Soy Home del Estudiante</h2>
                <nav>
                    <button onClick={() => this.cerrarSesion()}>Cerrar Sesi√≥n</button>
                </nav>
                <div>
                    <table>
                        <thead>
                            <tr>
                                <th>idGrupo</th>
                                <th>idEstudiante</th>

                            </tr>
                        </thead>

                        <tbody>
                            {this.state.data.map(grupo => {
                                return (
                                    <tr>
                                        <td>{grupo.idGrupo}</td>
                                        <td>{grupo.idEstudiante}</td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>
                </div>
            </>
        );
    }
}

export default HomeStudent;
